void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord*iResolution.xy*0.00008*cos(iTime);

    // Time varying pixel color
    vec3 col = tanh(vec3(uv.y)*vec3(uv.y))*sin(vec3(uv.x)-sin(iTime)) + cos(vec3(uv.y)+asin(iResolution) /vec3(uv.x))/sinh(vec3(uv.x)*sin(iTime))*iResolution+sin(iTime) ;

    // Output to screen
    fragColor = vec4(col,1.0)*sinh(iTime);

}

// sound
vec2 mainSound( int samp, float time)
{
  vec2 p = potato(samp,time);
  return p;
}

vec2 potato( int samp, float time)
{

    time = mod(sin(time),0.2); 
    float note = 1.10/tan(time);
    float pi = 3.14/time/1000.005+time*note;
    float frequency = 440.;
    time = time - sin(pi); 
    time = mod(time,0.2);
    return vec2(tan(time+note/pi + sin(frequency)))-sin(time)-0.00080;
}
