const float PI = 3.14159265359;
float circle(vec2 p, vec2 c, float r){
    return pow(dot(p - c, p - c), 1.0 + 0.5 * sin(iTime)) - r;
}
void mainImage( out vec4 fragColor, in vec2 fragCoord ){
    vec2 pos = (2.0 * fragCoord.xy -iResolution.xy)/ iResolution.yy;
    float w = circle(pos, vec2(0.0), 1.);
    vec3 v = vec3(step(mod(w, 0.3), 0.01));  
    v += mix(vec3(0, 0, 1), vec3(1, 0, 0), atan(w)/PI + 0.5);
    v *= 1.0-step(abs(w),0.01);
    fragColor = vec4(v, 1.0);
}
