void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord / iResolution.xy;

    // чем ближе к краям, тем больше эффект
    vec2 center_dist = 1.0 - abs(uv - .5) / .5;
    float arg = sin(iTime + fragCoord.y / 150.0);
    fragColor = texture(iChannel0, uv + arg / 30.0 * center_dist);
    //fragColor = vec4(center_dist.x);
}
