void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 uv = fragCoord / iResolution.xy;
    fragColor = texture(iChannel0, uv);

}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 uv = fragCoord/iResolution.xy;
    vec2 st = (2.* fragCoord - iResolution.xy) / min(iResolution.x, iResolution.y);
    vec2 vanDammeUv = clamp(
        abs(
            st
            * iResolution.yx / iResolution.x
        ),
        0., 1.
    );
	vec3 vanDammeTexture = texture(iChannel0, vanDammeUv).rgb;
    vec4 vanDammeColor = greenScreen(vanDammeTexture);
    vec3 previousColor = texture(iChannel1, uv).rgb;

    fragColor = vec4(
        mix(vanDammeColor.rgb, previousColor, vanDammeColor.a),
        1.0
    );
}

vec4 greenScreen(vec3 texel) {
    float maxrb = max( texel.r, texel.b );
    float k = clamp((texel.g - maxrb) * 5.0, 0.0, 1.0);
	float ll = length(texel);
    texel.g = min(texel.g, maxrb * 0.8);
    texel = ll * normalize(texel);
    return clamp(vec4(texel, k), 0., 1.);
}
