void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    int num_steps = 100;
    float speed = 0.1;
    float scale = 1.0 * (iTime*speed / (20.0+iTime*speed));

    vec2 uv = (fragCoord - vec2(0.0, iResolution.y / 2.0)) / iResolution.x;
    uv = uv * scale;
    uv = uv + vec2(0.25, 0);

    vec3 col = 0.0 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));

    vec2 z = vec2(0.0);
    float thresh = 4.0;
    bool flag = false;
    int i = 0;
    for (; i < num_steps; i++){
        z.x = pow(z.x, 2.0) - pow(z.y, 2.0) + uv.x;
        z.y = 2.0 * z.x * z.y + uv.y;
        if (pow(z.x, 2.0) + pow(z.y,2.0) > thresh) {
            flag = true;
            break;
        }
    }

    // Output to screen
    //fragColor = vec4(vec3(R < thresh), 1.0);
    //fragColor = vec4(z.x/10.0, z.y/10.0, 0.0, 1.0);
    if (flag) {
        float i = float(i);
        fragColor = vec4(0.2*sin(i/2.0)+0.2, 0.4*cos(i/10.0) + 0.5, i/11.0, 1.0);
        }

}
