// Reference shader that utilizes audio wave and fft data for glslViewer 
// (cross platform console-based GLSL Sandbox for 2D/3D shaders shaders):
// https://github.com/patriciogonzalezvivo/glslViewer.git
// The same glslViewer's shader can be found here:
// https://github.com/patriciogonzalezvivo/glslViewer/blob/master/examples/2D/00_tests/test_audio.frag
// Created by Sergei B (https://github.com/bespsm)


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;

	// fft
	float fft  = texture( iChannel0, vec2(uv.x,0.25)).x; 

    // wave
	float wave = texture( iChannel0, vec2(uv.x,0.75)).x;

	vec3 col = vec3(0.0);

	col += 1.0 -  smoothstep( 0.0, 0.01, abs(wave - uv.y) );

    col += 1.0 -  smoothstep( 0.0, 0.03, abs(fft - uv.y) );

    // green
    col = pow( col, vec3(1.0,0.5,2.0) );

	fragColor = vec4(col,1.0);
}
