// acid simplification of the new GLSL bug in https://shadertoy.com/view/lst3R7


    #define DEST U.x   // vec.x = float = expr doesn't work
//  #define DEST u     // float = float = expr doesn't work

void mainImage( out vec4 O,  vec2 U ) {

    vec2 R = iResolution.xy;

    float s=1., w = 1., u=U.x;

    if (U.y > R.y/2.)
        DEST *= s = sin( w );      // top: wrong result if DEST = vec.x

    else 
        s = sin( w ), DEST *= s;   // bottom: ref.

    O = vec4(.5+DEST/R.x);
}
