float randfunc(float x, float y)
{
    return 0.5 + 0.5 * cos(x + sin(2. * y + cos(x + iTime)) + cos(iTime + x + cos(4. * y)) + 10. * sin(x));
}

// 2d rotation matrix: rot(angle) * vector will rotate the vector by angle clockwise
mat2 rot(float angle)
{
    return mat2(cos(angle), -sin(angle), sin(angle), cos(angle));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy * 16.;

    vec2 uv_rotated = rot(iTime * 0.005) * uv;
    float val = randfunc(uv_rotated.x, uv_rotated.y);

    vec3 blue = vec3(0.6, 0.7, 0.9);
    vec3 red = vec3(0.75, 0.2, 0.5);
    vec3 col = mix(blue, red, val);

    // Output to screen
    fragColor = vec4(col,1.0);
}
