renderpass.code:
float letter(vec2 uv, vec2 incoord, vec2 off)
{
    float o;
    vec2 coor = uv*16. - off;
    if(coor.x < incoord.x || coor.y < incoord.y)
    return 0.;

    if(coor.x > incoord.x + 1. || coor.y > incoord.y + 1.)
    return 0.;

    //if(uv.x > incoord.x + 0.0625 || uv.y > incoord.y + 0.0625)
    //return 0.;

    o = smoothstep(0.,1.0,texture(iChannel0, uv - off/16.).r);
    return o;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    uv -= .5;
    uv *= .8;
    uv += .5;

    uv.x -= .125;
    uv.y += .2;

    uv.y += sin(iTime * 5. + uv.x * 15.)*.025;


    // Time varying pixel color
    vec3 col = vec3(0.0);
    col.r += letter(uv, vec2(7.,11.), vec2(-7.,0.)); //G
    col.r += letter(uv, vec2(15.,11.), vec2(-14.,0.)); //O
    col.r += letter(uv, vec2(15.,11.), vec2(-13.,0.)); //O
    col.r += letter(uv, vec2(4.,11.), vec2(-1.,0.)); //D

    col.r += letter(uv, vec2(13.,11.), vec2(-8.,0.)); //M
    col.r += letter(uv, vec2(15.,11.), vec2(-9.,0.)); //O
    col.r += letter(uv, vec2(2.,10.), vec2(5.,1.)); //R
    col.r += letter(uv, vec2(14.,11.), vec2(-6.,0.)); //N
    col.r += letter(uv, vec2(9.,11.), vec2(0.,0.)); //I
    col.r += letter(uv, vec2(14.,11.), vec2(-4.,0.)); //N
    col.r += letter(uv, vec2(7.,11.), vec2(4.,0.)); //G

    fragColor = vec4(col,1.0);
}
