void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    uv*=8.0;
    uv.x+=iTime*0.25;

    float x = (1.0 + cos(uv.x*6.28*2.0)) * 0.3;
    float y = (1.0 + sin(uv.y*6.28)) * 0.5;

    float col = (x+y);
    float phaseA = abs(tan(iTime+uv.y*2.0));
    col = smoothstep(col,0.1+phaseA*0.3,0.40);
    if(col > 0.8) {
        col = log(sin(col*0.1));
    }
    fragColor = vec4(vec3(col,col,0.1),1.0);
}
