struct Grid
{
    float rowsize;
    float columnsize;
    float linesize;
};

//returns true if given point is on Line / Row of grid
bool pointPartOfGrid(Grid grid, vec2 point)
{
    float columnAndLine = grid.columnsize+grid.linesize;
    float rowAndLine = grid.rowsize+grid.linesize;
    if(mod(point.x,columnAndLine)<grid.linesize)
    {
        return true;
    }
    if(mod(point.y,rowAndLine)<grid.linesize)
    {
        return true;
    }

    return false;
}

//returns true uv and mousxy is within the same grid cell
bool cellTouchedByMouse(Grid grid, vec2 uv)
{
    vec2 mouseUv = iMouse.xy/iResolution.xy;

    float columnAndLine = grid.columnsize+grid.linesize;
    float rowAndLine = grid.rowsize+grid.linesize;

    int column = int(uv.x/columnAndLine);
    int row = int(uv.y/rowAndLine);

    int mouseColumn = int(mouseUv.x/columnAndLine);
    int mouseRow = int(mouseUv.y/rowAndLine);

    if(column==mouseColumn && row==mouseRow)
    {
        return true;
    }
    else
    {
        return false;
    }
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    //set Colors
    vec3 lineColor = vec3(0,0,0);
    vec3 backColor = vec3(1,1,1);
    vec3 selectedColor = vec3(0,1,1);

    Grid grid;
    //set grid properties
    grid.rowsize=0.2;
    grid.columnsize=0.2;
    grid.linesize=0.01;

    vec3 col = backColor;
    if(pointPartOfGrid(grid,uv))
    {
        col = lineColor; 
    }
    else
    {
        if(cellTouchedByMouse(grid,uv))
        {
            col = selectedColor;
        }
    }
   fragColor = vec4(col,1.0);
}
