vec2 triangle_wave(vec2 a){
    return abs(fract((a+vec2(1.,0.5))*1.5)-.5);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    fragColor = vec4(0.0);
    vec3 col = vec3(0.);
    float t1 = 8.;
    vec2 uv = (fragCoord)/iResolution.y/t1/2.0 + vec2(iTime/2.0,iTime/3.0)/t1/8.0;
    vec2 t2 = vec2(0.);
    float p1 = sign(uv.x);
    for(int k = 0; k < 6; k++){
        p1 *= sign(uv.x);
        uv = (uv+t2/1.5);
        //if(uv.y>uv.x) uv = uv.yx; else uv *= -sign(uv.y-uv.x);
        t2 = -p1*triangle_wave(uv-1.5);
        uv = t2-p1*triangle_wave(uv.yx);

        col.x = (max(uv.y-uv.x,0.)-col.x);
        col = abs(col.yzx-col.x/3.);
    }
    fragColor = vec4(col*3.,1.0);
}
