vec4 makeGrey(vec4 texture)
{
    //takes colors from texture, and turns them into graysale
    vec4 color;
    vec4 returne = texture;
    color.rgb = texture.rgb;
    float mean = ((color.r + color.g + color.b) / 3.);
    returne.rgb = vec3(mean);
    return returne;
}

vec4 splitScreen(vec4 textura, vec4 texturo, vec2 uv)
{
    // splits the screen into the two textures
    vec4 tx1;
    tx1 = textura;
    vec4 tx2;
    tx2 = texturo;
    if(uv.x < .5)
    {
        return tx1;
    }
    else
    {
        return tx2;
    }
}

vec4 tintRed(vec4 texture)
{
    //takes colors from texture, and makes it red.
    vec4 color;
    vec4 returne = texture;
    color.rgb = texture.rgb;
    color.r *= 1.1;
    color.g *= .9;
    color.r *= .9;
    returne.rgb = color.rgb;
    return returne;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Prints out textures
    vec2 uv = fragCoord/iResolution.xy;
    vec4 text1 = texture(iChannel0, uv.xy);
