#define scale 0.1


float random(vec2 x) { 
    return fract( sin( dot(x ,vec2(12.12357, 20.789) ) + 0.001 ) * 223.123  );
}


float smooth_line(float x) {
    float f = 6.0 * pow( x , 5.0 ) -  15.0 * pow( x , 4.0 ) +  10.0 * pow( x , 3.0);
    return f;
}

/** @brief value noise */
float valueNoise(vec2 pos){

    vec2 fr= fract(pos);
    vec2 AA= floor(pos);
    vec2 AB= floor(pos)  + vec2(1,0);
    vec2 BA= floor(pos)  + vec2(0,1);
    vec2 BB= floor(pos)  + vec2(1,1);

    // we sample random number on a regular grid
    //
    //  AA-----------AB
    //   |           |
    //   |           |
    //   |    pos    |
    //   |           |
    //   |           |
    //  BA-----------BB
    float rAA = random(AA);
    float rAB = random(AB);
    float rBA = random(BA);
    float rBB = random(BB);

    // bilinear interpolation of the value
    float nAA_AB = mix(rAA , rAB,      (   fr.x));
    float nBA_BB = mix(rBA , rBB,      (  fr.x));
    return mix(nAA_AB , nBA_BB,      (  fr.y));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
  // float value = valueNoise(fragCoord * scale );

     vec2 uv = fragCoord/iResolution.xy  * 30.0 ;
     float value = valueNoise(uv );

    fragColor = vec4(  value , value,value , value );
}
