void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 p = 5.*(( fragCoord.xy-.5* iResolution.xy )/iResolution.y)-.5 ;
    vec2 i = p;
	float c = 0.0;
	float r = length(p+vec2(sin(iTime),sin(iTime*.222+99.))*1.5);
	float d = length(p);
	float rot = d+iTime+p.x*.915; 
	for (float n = 0.0; n < 7.0; n++) {
		p *= mat2(cos(rot-sin(iTime/1.)), sin(rot), -sin(cos(rot)-iTime), cos(rot))*-0.75;
		float t = r-iTime/(n+1.5);
		i -= p + vec2(cos(t - i.x-r) + sin(t + i.y),sin(t - i.y) + cos(t + i.x)+r);
		c += 1.0/length(vec2((sin(i.x+t)/.15), (cos(i.y+t)/.15)));
	}
	c /= 6.0;
	fragColor = vec4(vec3(c)*vec3(4.0, 4.0, 4.1)-0.35, .1);
}
