  #define H(a)   fract(1e4*sin(dot(ceil(a),vec2(17,71))))
  #define S(v)   smoothstep(20./R.y, 0., v ) 
//#define F3(h)  length(h)*cos(mod(atan(h.y,h.x),2.1)-1.05 - vec2(0,1.57) ) // --- 3-fold rotational symmetry
  #define F3(h)  length(h)*cos(mod(atan(h.y,h.x)+1.05*.5,2.1)-1.05*.5 - vec2(0,1.57) )

//#define P(h) S( abs(h.xyxy)-.1 )                   // --- play with the profile function !
