//#define P(h) .5+.5*cos(60.*length(F3(h)-.575*J.yx))
//#define P(h) S( abs( length(F3(h)-.575*J.yx) -.285 ) -.15 )
//#define P(h) S( abs( abs( length(F3(h)-.575*J.yx) -.285 ) -.15  ))
  #define P(h) .5*S( w = abs( length(F3(h)-.575*J.yx) -.285 ) -.15 ) + S( abs(w) )
//#define P(h) min(1., S( abs(length(vec2(abs(h.x)-.9,h.y))-.78)-w ) + 4.5*dot(h,h)* S(abs(h.y)-w))

void mainImage( out vec4 O, vec2 u )
{
    vec2 R =  iResolution.xy,
         U = 13.*(u-.5*R)/R.y + iTime*1.3,
         J = vec2(.5,.87),                                  // hexuv
         a = mod(U+J,J+J) - J,
         b = mod(U  ,J+J) - J,
         G = dot(a,a) < dot(b,b) ? a : b;                   // G: hex coords.  U-G : hex id

    float s = fract(1e4*sin(dot(ceil(U-G+floor(5.+iTime)),vec2(17,71)))), w=.08;  // tile angle attribute

    G *= mat2(cos( (s<.33 ? 3.14/3. : s<.66 ? -3.14/3. : 0.) + vec4(0,11,33,0))); // random rot tile
    O += P(G) -O;                                           // apply profile shape
 // O *= S(.5-length(O)) *s;                                // debug: show tile Id
}
