void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    //borderSize (adjust this)
    float borderSize = .05;
    //active Color
    vec3 activeColor = vec3(227./255., 196./255., 162./255.);

    //make the border
    float left = step(uv.x,borderSize * (iResolution.y/iResolution.x));
    float right = 1.0-step(uv.x,1.0-borderSize * (iResolution.y/iResolution.x));
    float top = step(uv.y,borderSize);
    float bottom = 1.0-step(uv.y,1.0-borderSize);
    float sDf = clamp( max(max(max(left,top),bottom),right),0.0,1.0);

    //multiply
    vec3 color = vec3(sDf)*activeColor;

    // Output to screen
    fragColor = vec4(color,1.0);
}
