    }*/


   //float m=smoothstep((fraction.x+0.01),fraction.x,fraction.y);


     color=vec3(smoothstep((fwidth(fraction.x)+0.01),fwidth(fraction.x),fraction.y));
     color+=vec3(smoothstep((fwidth(fraction.x)+0.01),fwidth(fraction.x),fraction.x));
     color.rb*=1.0-smoothstep((fwidth(a.x)+0.02),(fwidth(a.x)+0.01),abs(a.x));
