vec2 fixuv(vec2 c)
{
    return (3.0*(2.0*c-iResolution.xy)/iResolution.y);
}




void mainImage(out vec4 fragColor,in vec2 fragCoord)
{
    vec2 uv=fixuv(fragCoord);
    vec3 color=grid(uv);
    fragColor=vec4(color,0.0);    //要加vec4

}
