float plot(vec2 st) {    
    return smoothstep(0.02, 0.0, abs(st.y - st.x));
}

vec3 getGooeyLineRotation(vec2 uv, vec2 position, float zoom) {
    return vec3(plot(vec2(
        sin((uv.x + position.x) * zoom) * sin(iTime),
        cos((uv.y + position.y) * zoom) * cos(iTime)
    )));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy-.5;

    vec3 col = getGooeyLineRotation(uv, vec2(0., 0.), 10.)
    + getGooeyLineRotation(uv, vec2(.5, .5), 10.)
    + getGooeyLineRotation(uv, vec2(1., 1.), 10.);

    // Output to screen
    fragColor = vec4(col,1.0);
}
