float random (vec2 st) {
    return fract(sin(dot(st.xy+iTime,vec2(12.9898,78.233)))*42323.211); // функция, выдающая рандомное число
}

vec2 CoordinateOffsetX(vec2 uv, float c)
{

    vec2 coordinate = uv;
    coordinate.x += c * random(vec2(iTime, iTime+coordinate.y))* 0.1;// рандомное смещение координаты пикселя по x
    coordinate.x = mod(coordinate.x,1.0);
    return coordinate;
}

vec3 getVideo(vec2 uv, float c)
{
    vec2 look = CoordinateOffsetX(uv, c);
	vec3 video = vec3(texture(iChannel0,look)); // выходной цвет текстуры в указанных координатах
	return video;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord.xy/iResolution.xy;
    float c;// создаем коэфициент
    c = uv.y + 0.016 + iTime/6.0; 
    c = mod(c, 1.0);
    c = step(c, 0.026);
    vec3 video = getVideo(uv, c);

    fragColor = vec4(video,0.0);
}
