void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.yy;

    vec2 fv1 = uv - vec2(0.35, 0.5);
    float r1 = length(fv1);
    float f1 = 0.005/(0.0001 + r1*r1);
    fv1 = normalize(fv1) * f1;

    vec2 fv2 = uv - vec2(0.75, 0.5);
    float r2 = length(fv2);
    float f2 = 0.001/(0.0001 + r2*r2);
    fv2 = normalize(fv2) * f2;

    vec2 cf = (uv - vec2(0.5, 0.5));
    float cr = length(cf);
    cf = normalize(cf) * (0.25 * cr);

    vec3 col = mix(vec3(0.,0.,0.),vec3(1.,1.,1.), sin(10. * length(fv1 + fv2 - cf)));


    // Output to screen
    fragColor = vec4(col,1.0);
}
