#define PI 3.1415926

float aspect;

float makeStrip(float threshold, float x, vec2 uv, float off) {
    float diff = 0.02;

    float strip = step(threshold, x) - step(threshold + diff, x);
    // strip *= step(cos(iTime) * 0.5 + 0.5, uv.y);
    // strip *= fract(iTime + (uv.y * 0.5) + off) < 0.5 ? 1.0 : 0.0;
    // strip *= sin(iTime + uv.y + off) < 0.0 ? 1.0 : 0.0;

    return strip;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    // Normalized pixel coordinates (from 0 to 1)
    vec2 st = fragCoord/iResolution.xy;
    aspect = iResolution.x / iResolution.y;


    float c = makeStrip(0.200, st.x * aspect, st, PI * 0.0) +
              makeStrip(0.700, st.x, st, PI * 1.0) +
              makeStrip(0.925, st.x, st, PI * 1.5) +
              makeStrip(0.800, st.y, st, PI * 1.15) +
              makeStrip(0.100, st.y, st, PI * 1.35) +
              makeStrip(0.600, st.y, st, PI * 1.10);

    // c = makeStrip(0.200, st.x, st);            

    // Output to screen
    fragColor = vec4(vec3(c),1.0);
}
