void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = vec2(1.0 - fragCoord.x/iResolution.x, fragCoord.y/iResolution.y);

    // Time varying pixel color
    vec3 col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));

    // Output to screen
    vec4 frame1 = texture(iChannel0, uv);
    float d1 =  0.025 * (cos(iTime / 2.0) - 0.5);
    float d2 =  0.025 * (sin(iTime / 4.0) - 0.5);
    vec4 frame2 = texture(iChannel0, vec2(uv.x - d1, uv.y + d2));
    vec4 frame3 = texture(iChannel0, vec2(uv.x - d2, uv.y + d1));
    float mixFactor1 = fract(sin(iTime / 2.0) + 0.75);
    float mixFactor2 = fract(sin(iTime / 4.0) + 0.25);
    fragColor = mix(mix(frame1, frame2, mixFactor1), frame3, mixFactor2);
    //fragColor = mix(fragColor, texture(iChannel1, uv), 0.5);
    //fragColor = mix(fragColor, texture(iChannel2, uv), 0.5);
    //fragColor = mix(fragColor, texture(iChannel3, uv), 0.5);
    fragColor *= vec4(col, 1.0);
}
