#define hash(p ) ( 2.* fract(sin((p)*mat2(127.1,311.7, 269.5,183.3)) *43758.5453123) - 1. )
#define grad(x,y)  dot( hash( i+vec2(x,y) ), f-vec2(x,y) )
#define Ierf(x) ( atanh(x) / 1.2 )          // not so bad reciprocal
#define keyToggle(a)  ( texelFetch(iChannel3,ivec2(a,2),0).x > 0.)

float noise( vec2 p )
{
    vec2 i = floor(p), f = fract(p), u = f*f*(3.-2.*f); // smoothstep
    return mix( mix( grad(0,0), grad(1,0), u.x),
                mix( grad(0,1), grad(1,1), u.x), u.y);
}

float perlin( vec2 U ) { // inspired from https://www.shadertoy.com/view/XdXGW8
    float v = 0., s = .5;
    mat2 m = mat2( 1.6,  1.2, -1.2,  1.6 );
    for( int i=0; i<4; i++, s/=2., U*=m )
        v  += s * noise( U ); 
    return v;
}


void mainImage( out vec4 O, vec2 u )
{
    vec2 R = iResolution.xy,
         U = u / R.y,
         P = 8.*U - vec2(0.,.5*iTime);
      // P = 8.*U - vec2(iTime,0);         // also interesting to see pattern morphing

	float f = keyToggle(32) ? perlin( P ) : noise( P );
    float m = iMouse.z > 0. ? 2.*iMouse.x/R.x :  2.*u.x/R.x;

    f = sign(f)*pow(abs(f),exp2(m)-1.) *exp2(m);

    if (U.y > .5) 
        f *= 8.,                                            // plot isolines
        O = vec4(1. - abs(fract(f)-.5)/fwidth(f));
    else
        O = vec4( .5+.5*f );
    O = sqrt(O);                                            // to sRGB

}
