//change these constants to get different patterns!
#define c1 vec3(1.,0.5,1.5)

vec2 triangle_wave(vec2 a,float scale){
    //a = abs(a);
    return abs(fract((a+c1.xy)*scale)-.5);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    fragColor = vec4(0.0);
    vec3 col = vec3(0.);
    float t1 = 16.;
    vec2 uv = (fragCoord)/iResolution.y/t1/2.0;
    uv += vec2(iTime/2.0,iTime/3.0)/t1/8.0;
    float scale = c1.z;
    vec2 t2 = vec2(0.);
    for(int k = 0; k < 9; k++){
        //uv -= (t2.yx)/scale;
        uv -= (t2.yx);
        //uv -= (t2.yx)*(.25+(t2.x+t2.y));

        t2 = triangle_wave(uv.yx-.5,scale);
        //t2 = triangle_wave(uv.yx-.5,scale)/(1.+t2);

        //t2 = triangle_wave(uv.yx-.5,scale)*(1.-t2/3.);


        uv.yx = (triangle_wave(uv,scale)-t2)/scale;
        col.x = abs(uv.y+uv.x-col.x);
        col = abs(col.yzx);
        //col.x = abs(uv.y+uv.x-col.x*2.);
        //col = abs(col.yzx-1.);
    }
    fragColor = vec4(col,1.0);   
}
