void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    fragColor = vec4(uv.y > sin(uv.x * 30.0 + iTime) / 8.0 + 0.5, 0, 0, 1) * 0.6;
    fragColor.g += float(uv.y > sin(uv.x * 30.0 + iTime + sin(iTime * 3.0) / 3.0) / 8.0 + 0.5) * 0.6;
    fragColor.b += float(uv.y > sin(uv.x * 30.0 + iTime) / (8.0 + cos(iTime * 2.0)) + 0.5) * 0.6;
    fragColor.rb += float(uv.y > sin(uv.x * 30.0 + iTime) / (8.0 + cos(iTime * 6.0)) + 0.5) * 0.3;
    fragColor.gb += float(uv.y > sin(uv.x * 30.0 + iTime) / (8.0 + sin(iTime * 4.0)) + 0.5) * 0.4;
    return;
}
