float newM(vec2 uv, float m) {
    float m2 = 0.4 + 0.12 * cos(9. * length(uv) + 8. * atan(uv.y, uv.x) - iTime);
    return 0.5 * mix(m, m2, 0.5);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord - 0.5 * iResolution.xy) / iResolution.y;
    uv *= 0.8 + 0.1 * cos(iTime);
    float th = -0.1 * iTime;
    mat2 M = mat2(cos(th), sin(th), -sin(th), cos(th));
    uv = abs(M * uv);

    float m = 4. * newM(uv, 0.);
    for (float i = 0.; i < 4.; i++) {
        th += 4. * mlength(uv);
        M = mat2(cos(th), sin(th), -sin(th), cos(th));
        uv = abs(M * (uv - m));
        m = newM(uv, m);
    }

    float d = length(uv - m );

    float k = 0.75 * m;//0.75/iResolution.y;
    float s = smoothstep(-k, k, -d + m);
    //s = 2. * s * s;
    vec3 e = vec3(1.);
    vec3 col = s * pal(s * 2. * s + 0.15 * iTime, e, e, e, 0.4 * vec3(0.,0.33,0.66));

    fragColor = vec4(col,1.0);
}

#define pi 3.14159

float thc(float a, float b) {
    return tanh(a * cos(b)) / tanh(a);
}

float ths(float a, float b) {
    return tanh(a * sin(b)) / tanh(a);
}

vec2 thc(float a, vec2 b) {
    return tanh(a * cos(b)) / tanh(a);
}

vec2 ths(float a, vec2 b) {
    return tanh(a * sin(b)) / tanh(a);
}

vec3 pal( in float t, in vec3 a, in vec3 b, in vec3 c, in vec3 d )
{
    return a + b*cos( 6.28318*(c*t+d) );
}

float h21 (vec2 a) {
    return fract(sin(dot(a.xy, vec2(12.9898, 78.233))) * 43758.5453123);
}

float mlength(vec2 uv) {
    return max(abs(uv.x), abs(uv.y));
}
