void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    vec4 channelAColor = texture(iChannel0, uv);
    vec4 textureBColor = texture(iChannel1, uv);

    // Time varying pixel color
    vec3 col = 0.5 + 0.5*cos(iTime * 2.0+uv.xyx+vec3(0,2,4));

    float lerpFactor = (sin(iTime * 5.0) + 1.0) * 0.5;
    // Output to screen
    fragColor = mix(channelAColor, textureBColor, lerpFactor) * vec4(col,1.0);
}

