vec3 pal( in float t, in vec3 a, in vec3 b, in vec3 h, in vec3 d )
{
    return a + b*cos( 6.28318*(h*t+d) );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec3 a = vec3(0,0,0);
    vec3 b = vec3(0,1,0);
    vec3 h = vec3(0,0.3,0);
    vec3 d = vec3(0,0,0);
    float dt = 1.0;
	vec2 p = fragCoord.xy / iResolution.xy;
    vec3 col = pal(p.y * dt, a, b, h, d);
	fragColor = vec4( col, 1.0 );
}
