#define TWO_PI 6.28318530718
const int count = 50;

// rand function from book of shaders: https://thebookofshaders.com/10/
float rand (vec2 co) {
    return fract(sin(dot(co.xy, vec2(12.9898,78.233))) * 43758.5453123);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{    
    vec2 co = fragCoord/iResolution.xy;

    float closestDist = length(vec2(1));
    int index = 0;
    for (int i=0; i<count; i++) {
        vec2 pos = vec2(
            rand(vec2(i + 1)),
            rand(vec2(i))
        ) + 0.1 * vec2(co.x * sin(iTime/5. + TWO_PI * rand(vec2(i))), co.y * cos(iTime/5. + TWO_PI * rand(vec2(i)))); // random position based on index + mouse click

        float dist = length(pos - co);
        if (dist < closestDist) {
            closestDist = dist;
            index = i;
        }
    }

    vec3 col = vec3(rand(vec2(index)));
    fragColor = vec4(col, 1) * vec4(0., 0., 1., 1.);
}
