void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    float axis = 1.0; // 1~0
    float pixelSize = 4.0;

    float skyGap = 0.5; // 0~1

    uv.y = floor(fragCoord.y/pixelSize)*pixelSize/iResolution.y;
    uv.y += skyGap;
    uv.x += iTime*(axis-uv.y);

    fragColor = texture(iChannel0,uv);

    if ( uv.y > 1.0 ){ // Background ( Sky? )
        //fragColor = vec4(0.0);
        fragColor = vec4(step(uv.y-cos(iTime+fragCoord.x),0.3))*vec4(sin(iTime));
    }
}
