float rand(vec2 c){
	return fract(sin(dot(c.xy ,vec2(12.9898,78.233))) * 43758.5453);
}
const float screenWidth =   1.;
const float PI = 3.1415926;
float noise(vec2 p, float freq ){
	float unit = screenWidth/freq;
	vec2 ij = floor(p/unit);
	vec2 xy = mod(p,unit)/unit;
	//xy = 3.*xy*xy-2.*xy*xy*xy;
	xy = .5*(1.-cos(PI*xy));
	float a = rand((ij+vec2(0.,0.)));
	float b = rand((ij+vec2(1.,0.)));
	float c = rand((ij+vec2(0.,1.)));
	float d = rand((ij+vec2(1.,1.)));
	float x1 = mix(a, b, xy.x);
	float x2 = mix(c, d, xy.x);
	return mix(x1, x2, xy.y);
}
void mainImage( out vec4 fragColor, in vec2 fragCoord ){
    vec2 coord = 6.0 * fragCoord.xy / iResolution.xy;
    coord.x *= iResolution.x/iResolution.y;
    coord += noise(coord,0.25);
    coord += noise(coord,0.50);
    coord += noise(coord,0.75);
    for(int i = 1;i < 190;i++){
        float n = float(i);
        coord += vec2(0.7 / n * sin(n * coord.y + iTime  + 0.3) + 0.8, 0.4 / n * sin(n * coord.x + iTime + 0.3 ) + 0.6);
    }
    vec3 color = vec3(0.5 * sin(coord.x) + 0.5, 0.5 * sin(coord.y) + 0.5,sin(coord.x+coord.y));
    fragColor = vec4(color,1.0);
}
