// probably the 1000st time this has been done

const float speed = 0.6;
const float frequency = 55.0;
const float amplitude = 0.02;

float wave(float infrequency, float inspeed, float inamplitude, float offset) {
    return sin(iTime * inspeed + offset * infrequency) * (inamplitude);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    float xWave = wave(frequency, speed, amplitude, uv.y);

    fragColor = texture(iChannel0, uv + vec2(xWave, 0));
}
