float b(vec2 u) {
    return 1./(dot(u,u)+1.);
}
void mainImage(out vec4 O, in vec2 I) {
	vec2 u = 2.*(I.xy/iResolution.xy)-1.;
    vec3 c = vec3(0),
    m[3];
    m[0] = vec3(1.5, .7, .7);
    m[1] = vec3(.75, 1., 1.25);
    m[2] = vec3(1.5, 1.25, .5);
    for (float i = 0.; i < 3.; i++) {
        float t = i*1.04+iTime*.35;
        vec2 o = vec2(.25 + sin(t*(5.)) * .3, sin(t*8.) * .3);
        c += b((u - o) * 55.) * m[int(i)];
    }
	O = vec4(c, 1.);
}

