#define R iResolution.xy
vec2 rotate(vec2 v, float a) {
	float s = sin(a);
	float c = cos(a);
	mat2 m = mat2(c, -s, s, c);
	return m * v;
}
float S (float c) {
    return smoothstep(1.5/R.y, 0., c);
}
#define PI 3.14159265
void mainImage(out vec4 O, in vec2 I){
    vec2 u = (I-.5*R)/R.y;
    float c = 0.,
          r = .38;
    for (float i = 0.; i < 8.; i += 1.) {
        float m = i/8. * PI * 2.;
        u = rotate(u, m);
        float t = 7./8.*PI*2.;
        float d = S(length(u - vec2(r*sin(t), r*cos(t)))-r);
        d -= S(length(u - vec2(r*sin(0.), r*cos(0.)))-r);
        d *= cos(length(u - vec2(r*sin(t), r*cos(t)))*193.) * S(length(u - vec2(r*sin(t), r*cos(t)))-r);
        c += d;
    }
    O = vec4(c*S(length(u)-r+0.035));
}
