void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = (2.0*fragCoord - iResolution.xy)/iResolution.y;

    float a = 3.141592;
    float theta = atan(abs(uv.y), uv.x);
    float d;

    if (theta>=0.0 && theta < a/4.0) {
        d = uv.x;
    } else if (theta >= a/4.0 && theta < a/4.0 * 3.0) {
        d = abs(uv.y);
    } else if (theta >= a/4.0 * 3.0 && theta < a) {
        d = abs(uv.x);
    }
    // Time varying pixel color
    float varyingY = mod(iTime, 10.)/15.;
    vec3 col = texture(iChannel0, vec2(varyingY, d-varyingY)).xyz;

    // Output to screen
    fragColor = vec4(col,1.0);
}
