vec2 mul(vec2 a, vec2 b) {
    return vec2(a.x*b.x-a.y*b.y, a.x*b.y + a.y*b.x);
}

vec2 f(vec2 x) {
    return mul(mul(x-vec2(1.0,1.0), x-vec2(1.0,-1.0)), x-vec2(-1.0,0.0));
}

vec2 fp(vec2 x) {
    return mul(x, 3.0*x-5.0*sin(0.5*iTime));
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 u = vec2(
        fragCoord.x - iResolution.x / 2.0,
        fragCoord.y - iResolution.y / 2.0
    ) / min(iResolution.x, iResolution.y);
    for (int i = 0; i < 7; i++) {
        u = u - f(u)/fp(u);
    }
    fragColor = vec4(10.0/abs(u.x), 10.0/abs(u.y), 0.0, 1.0);
}
