renderpass.code:
// bottle profile
#define B(h) (  h > 1.02 ? -99. : h > 1. ? .1*R((h-1.)/.02) : h > .6 ? .1 \
              : h > .4 ? .1+.3*H(1.-(h-.4)/.2) : h > -.4 ? .4             \
              : h > -.5 ? .4*R((h+.4)/.1)  : -99.                         \
             )                                                           //
#define H(x) ( (x)*(x)* ( 3. - 2.*(x) )  )                               //
#define R(x)  sqrt(1.-(x)*(x))                                           //
#define S smoothstep
void mainImage(out vec4 O, vec2 u)
{
    vec2  R = iResolution.xy,
          U = ( 2.*u - R ) / R.y;
    U.y += .3;
    float x = abs(U.x), y = U.y,
          p =  B(y), v = p - x,                              // dist to profile
          w = B(y-.1) - x;                                   // offset version for bottom
          v /= fwidth(v), w /= fwidth(w);                    // for AA
    O = mix( S( -1.,1.,v )                                   // profile
             * (   y > .8 ? vec4(.4,0,.2,1)                  // cork
                 : y > .5 ? vec4(.9,.97,1,1)                 // air
                 : pow( vec4(1,.5,.1,0), vec4(.7*R(x/p)) )   // juice
               ),
             vec4(.4,0,.2,1),                                // label
             step(x,.3) *S(2./R.y,0.,abs(y-.05+.04*R(x/.3))-.17) 
           )
       * (.5+.5*min(S( 0.,3.,abs(v-1.) ),S(-1.,1.,w+.4)) );  // thick glass
    v = .5*p + U.x;  v /= fwidth(v);
    if ( y > -.45 && y < 1.01) O += .8*S( 5.,0.,abs(v-1.) ); // reflect
}
