#define R    iResolution.xy
#define S(v) smoothstep(6./R.y, 0., v)               // AA draw

float L(vec2 p, vec2 a,vec2 b) {                     // line drawing
    p -= a, b -= a;
    return S( length(p - b * clamp(dot(p, b) / dot(b, b), 0., 1.) ) );
}

void mainImage( out vec4 O, vec2 U )
{
    U = 2.* ( U+U - R ) / R.y;
    float d = .33, o = .6,
          x = U.x, v = cos(x+2.*iTime)/5.,
          y = U.y = abs(U.y);                        // symmetries: 
              U.x = abs(fract(U.x*6.+.5)-.5)/6.;     // consider only top half columns

    O = vec4(1);
    O -= L(U, vec2(0,0),vec2(0,1.5) )                // color bars
         * ( abs(y-o) > d ? vec4(.4,1,1,0) : vec4(1,1,.4,0) )
      +  L(U, vec2(0, 1.5),vec2(.07,1.5-v) )         // top half-arrow
      +  L(U, vec2(0, o+d),vec2(.07, o+v+d) )        // mid
      +  L(U, vec2(0, o-d),vec2(.07, o-v-d) );       // bottom ( in top half )

    if (iMouse.z>0.)                                 // if click
        O = mix(O,vec4(0,1,0,0), S(min(abs(y-1.5), abs(abs(y-o)-d)) )); // green lines

    O = sqrt(O);                                     // to sRGB
}
