#define PI 3.14159265358979323846

vec3 screen(vec3 base, vec3 bl_layer, float alpha){
    return base + alpha*(bl_layer - base * bl_layer);
}

vec2 rotate2D(vec2 _st, float _angle){
    _st -= 0.5;
    _st =  mat2(cos(_angle),-sin(_angle),
                sin(_angle),cos(_angle)) * _st;
    _st += 0.5;
    return _st;
}

vec2 scale2D(vec2 _st, vec2 _scale){
    _st -= 0.5;
    _st = mat2(_scale.x,0.0,
               0.0,_scale.y) * _st;
    _st += 0.5;
    return _st;

}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    vec4 tex0 = texture(iChannel0, uv);

    vec3 col1 = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));
    vec3 col2 = 0.5 + 0.5*sin(iTime+uv.xyx+vec3(0,2,4));
    vec3 col3 = 0.5 + 0.5*cos(iTime+uv.yxy+vec3(0,2,4));

    vec2 st = uv;
    st = rotate2D(scale2D(st, vec2(0.75 + 0.025*sin(iTime))), 0.025*cos(iTime));
    vec3 layer1 = vec3(texture(iChannel1, st).r*col1);

    st = uv;
    st = rotate2D(scale2D(st, vec2(0.79 + 0.047*cos(iTime))), 0.045*cos(iTime));
    vec3 layer2 = vec3(texture(iChannel1, st).g*col2);

    st = uv;
    st = rotate2D(scale2D(st, vec2(0.7 + 0.047*cos(iTime))), 0.045*cos(iTime*0.5));
    vec3 layer3 = vec3(texture(iChannel1, st).b*col3);

    vec3 layers = screen(screen(layer1, layer2, 1.), layer3, 0.3);

    // Output to screen
    fragColor = vec4(screen(screen(tex0.rgb,col1, 0.15), layers, 1.), 1.);
    //fragColor = vec4(vec3(layer3), 1.0);
}
