float checker(vec2 p) {
    float xg = trunc(mod(p.x, 1.0) * 2.0);
    float yg = trunc(mod(p.y, 1.0) * 2.0);
    return (yg <= 0.0) ? xg : (xg <= 0.0) ? yg : 0.0;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 uv = (2.0 * fragCoord - iResolution.xy) / iResolution.x * 2.0;

    uv.y += iTime / 2.0;

    fragColor = vec4(vec3(checker(uv)), 1.0);
}
