#define PI 3.14159265358979323846

//Screen
vec3 screen(vec3 base, vec3 bl_layer){
    return base + bl_layer - base * bl_layer;
}

vec3 multiply (vec3 base, vec3 bl_layer){
    return base*bl_layer;
}

vec2 rotate2D(vec2 _st, float _angle){
    _st -= 0.5;
    _st =  mat2(cos(_angle),-sin(_angle),
                sin(_angle),cos(_angle)) * _st;
    _st += 0.5;
    return _st;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;


    vec4 tex0 = texture(iChannel0, uv);
    uv = rotate2D(uv, 0.);// set PI*0.5 instead of 0. if screen is portrait oriented
    uv.x += sin((iTime/5.))*0.05;
    vec4 tex1 = texture(iChannel1, (uv/1.2+0.1)+sin(iTime/4.)*0.0251);



    // Output to screen
    fragColor = mix(tex0, vec4(multiply(tex0.rgb, tex1.rgb), 1.0), 1.0);

}
