// you can change the time interval in the common tab to observe each CA evolving from a different start frame or time
// buffer A is the smart one, having insight into the future. buffer D starts 1st, then C, B and finally A.

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    vec2 uv = fragCoord.xy/iResolution.xy;

    float window = 0.0f;

    vec3 a = textureLod(iChannel0, uv * 2.0f, 0.0f).rgb;
    vec3 b = textureLod(iChannel1, uv * 2.0f, 0.0f).rgb;
    vec3 c = textureLod(iChannel2, uv * 2.0f, 0.0f).rgb;
    vec3 d = textureLod(iChannel3, uv * 2.0f, 0.0f).rgb;

    vec3 color = vec3(0);

    color = mix(mix(a, b, uv.xxx), mix(c, d, uv.xxx), uv.yyy);

    const float sz = 0.25f;
    float text = 0.0f;
    draw_string(text, vec2(0.0f, 0.0f) * sz, uv * sz, int[](lo_a), RES);
    draw_string(text, vec2(0.5f, 0.0f) * sz, uv * sz, int[](lo_b), RES);
    draw_string(text, vec2(0.0f, 0.5f) * sz, uv * sz, int[](lo_c), RES);
    draw_string(text, vec2(0.5f, 0.5f) * sz, uv * sz, int[](lo_d), RES);
    color += text;

    fragColor = vec4( color, 1. );
}

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    vec2 uv = fragCoord/iResolution.xy;

    float state = 0.0f;

    state = sim(iChannel0, fragCoord, iResolution.xy);

    if (iFrame < INTERVAL * 4) {
        state = seed(iChannel1, fragCoord, iChannelResolution[1].xy);

        const float sz = 0.05f;
        float text = 0.0f;
        draw_string(text, vec2(0.25f, 0.4f) * sz, uv * sz, int[](lo_a, hi_U, hi_T, hi_O), RES);
        state *= text;
    }

    float future_state = textureLod(iChannel3, uv, 0.0f).r;

    float mini, maxi;
    mini = min(state, future_state);
    maxi = max(state, future_state);

    state = mix(state, future_state, (maxi - mini) * 0.5f);;

    fragColor = vec4(state * vec3(1), 1.0f);
}

