vec2 tile(vec2 st, float scale, float t){
    st *= scale;

    float a;
    float smoothT = fract(t); 
    smoothT = 3.0*pow(smoothT, 2.0) - 2.0*pow(smoothT, 3.0);
    //smoothT *= smoothT*(3.-2.0*smoothT);
    if (mod(t, 2.0) > 1.0) {
        // Move vertical
        a = mod(st.x, 2.0) > 1.0 ? 1.0 : -1.0;
        st.y += a*smoothT;
    } else {
        // Move horizontal
        a = mod(st.y, 2.0) > 1.0 ? 1.0 : -1.0;
        st.x += a*smoothT;
    }

    return fract(st);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.y;

    uv = tile(uv, 15.0, iTime);

    // Time varying pixel color
    float d = length(uv - vec2(0.5, 0.5));
    float pix = 22.0/iResolution.y;
    vec3 col = vec3(smoothstep(0.3, 0.3 + pix, d));

    // Output to screen
    fragColor = vec4(col,1.0);
}
