const float s = 0.01;

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    // Normalized pixel coordinates with the origin in the center
    vec2 uv = fragCoord - iResolution.xy / 2.;

    float v = 1004. - 1000. * cos(iTime / 10.);

    float d = sqrt(uv.x * uv.x + uv.y * uv.y) * v * s;

    // pixel color
    vec3 col = vec3(0.5 + 0.5 * cos(d));

    // Output to screen
    fragColor = vec4(col, 1.0);
}
