#define PI (3.14159265358979)
#define STRIPECOUNT 4.0
#define DENSITY 1.0
#define COLORSMOOTH 0.2
#define STRIPESMOOTH 0.9

vec3 getColor(float r) {
    r = fract(r);
    float mix0 = smoothstep(0.00, 0.00 + COLORSMOOTH, r);
    float mix1 = smoothstep(0.25, 0.25 + COLORSMOOTH, r);
    float mix2 = smoothstep(0.50, 0.50 + COLORSMOOTH, r);
    float mix3 = smoothstep(0.75, 0.75 + COLORSMOOTH, r);

    vec3 color0 = vec3(1.0, 0.7, 0.9);
    vec3 color1 = vec3(1.0, 0.5, 0.6);
    vec3 color2 = vec3(0.3, 0.5, 0.7);
    vec3 color3 = vec3(0.5, 0.6, 0.9);

    return (
        color0 * (mix0 - mix1) +
        color1 * (mix1 - mix2) +
        color2 * (mix2 - mix3) +
        color3 * (mix3 - mix0 + 1.)
    );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float time = fract(iTime / 6.);
    // Scales coords so that the diagonals are all dist 1 from center
    float scale = length(iResolution.xy);
    vec2 uv = (fragCoord / scale
	- (iResolution.xy / scale / 2.)) * 2.;

    vec4 noise = texture(iChannel0, vec3(
        uv.x * 0.8 + uv.y * 0.1, 
        uv.x * 0.1 + uv.y * -0.8, 
        time + uv.x * -0.3 + uv.y * 0.3
    ));

    // wiggle coords using prebaked noise
    uv += 0.03 * (noise.rg - 0.5);

    // for wormhole or perspective effect
    float r = log(length(uv)) + (1.0 + 0.2 * sin(time * 2. * PI));

    float flashy = ((time - 0.5) * (time - 0.5)) * 26.0;


    // if you want the angle in range [0, 1) and not (-π, π]
    // divide angle by 2pi and mod1 it
    float theta = fract(atan(uv.y, uv.x) / 6.2831853071795) + noise.a * 0.1;

    float ofs_raw = ((time + r * 0.5 + theta) * STRIPECOUNT);

    float ofs = fract((
        floor(
            ofs_raw
        ) + smoothstep(
            0.0,
            STRIPESMOOTH, 
            fract(ofs_raw)
        ) + floor(flashy * flashy)
    ) / STRIPECOUNT);
    float rings = r * DENSITY;


    // Time varying pixel color
    vec3 col = getColor(ofs + rings) + noise.bbb * 0.1;
    //vec3 col = vec3();

    // Output to screen
    fragColor = vec4(col,1.0);
}
