float drawDisc(vec2 coord,float radiusOut){
    return step(length(coord),radiusOut);
}
float drawCircle(vec2 coord, float radiusOut, float radiusIn){
    return drawDisc(coord,radiusOut) - drawDisc(coord,radiusIn);
}
float drawDiscSmooth(vec2 uv, float borderThickness, float radius){

    return 1.-smoothstep(0.,borderThickness,abs(radius-sqrt(dot(uv,uv))));
}

