float metaball( vec2 uv, vec2 ball )
{
    return 1. / length( uv - ball );
    // return inversesqrt( pow( ( uv.x-ball.x ), 2.0 ) + pow( ( uv.y-ball.y ), 2.0 ) );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    //fragColor = vec4( 0., 0., 0., 1. );

    vec2 uv = fragCoord / iResolution.y;

    vec2 ball_1 = vec2( 0.2, 0.4 );

    float ang = mod( iTime * 2., 2. * 3.14 );

    vec2 ball_2 = vec2( 0.26, 0.4 ) + ( 1. + vec2( sin( ang ), cos( ang ) ) ) * 0.25;

    //ball_2 = vec2( 0.8, 0.7 );

    vec2 ball_3 = iMouse.xy / iResolution.y;

    float ball_1m = metaball( uv, ball_1 );
    float ball_2m = metaball( uv, ball_2 );
    float ball_3m = metaball( uv, ball_3 );

    float balls = ball_1m + ball_2m + ball_3m;

    //float color = step( 15., balls );

    float color = smoothstep( 14.5, 15., balls );

    fragColor = vec4( vec3( color ), 1. );

    //if ( ball_1m + ball_2m + ball_3m > 15. )
    //{
    //    fragColor = vec4( 1., 1., 1., 1. );
    //}
}
